def save_ticker_info():
    print("There is: ", len(current_tickers), " tickers now.")
    # File path where you want to save the dictionary

    data = {}
    for idx,ticker in enumerate(current_tickers):

        if idx %500 == 0 and idx != 0: 
            print("taking a breather.....")
            time.sleep(60) 

        data[ticker]={"industry":"","sector": "", "country":"","name":""}
        try: 
            tick = yf.Ticker(ticker)
            infos = tick.info
            
            try:
                # data[ticker]={"industry":infos['industry'],"sector": infos["sector"], "country":infos['country']}
                data[ticker]["industry"] =  infos['industryKey']  
                print("key ", infos['industryKey'] )
            except: 
                try: 
                    print("unformatted industry ",infos['industry'])
                    formatted_string = infos['industry'].lower()
                    formatted_string = formatted_string.replace('—','-')
                    formatted_string = formatted_string.replace(' ', '-') 
                    formatted_string = formatted_string.replace('&', '-') 

                    formatted_string = '-'.join(filter(None, formatted_string.split('-')))
                    data[ticker]["industry"] =  formatted_string  
                    print("formated industry", formatted_string)
                except: 
                    print(f"error looking for industry for {ticker}")

            try:
                # data[ticker]={"industry":infos['industry'],"sector": infos["sector"], "country":infos['country']}
                data[ticker]["sector"] =  infos['sectorKey']  
                print("key ", infos['sectorKey'])
            except: 
                try: 
                    formatted_string = infos['sector'].lower()
                    formatted_string = formatted_string.replace('—','-')
                    formatted_string = formatted_string.replace(' ', '-') 
                    formatted_string = formatted_string.replace('&', '-') 

                    formatted_string = '-'.join(filter(None, formatted_string.split('-')))
                    data[ticker]["sector"] =  formatted_string 
                    print("formated sector ", formatted_string)
                except: 
                    print(f"error looking for sector for {ticker}")
                    
            try: 
                data[ticker]["country"] =  infos['country']  
            except:
                print(f"error looking for country for {ticker}")

        except: 
            print(f"general error looking for {ticker}")
            continue

    file_path = TICKER_INFO_PATH
    # Save the dictionary to a file in JSON format
    with open(file_path, 'w') as file:
        json.dump(data, file)

    print("===== done saving..!")

def retrieve_ticker_info(self):
    file_path = TICKER_INFO_PATH

    with open(file_path, 'r') as file:
        loaded_data = json.load(file)

    #Now we can add the name 
        
    for ticker in loaded_data.keys():
        try:
            loaded_data[ticker]["name"] =  self.ticker_symbol_name_map[ticker] 
        except:
            loaded_data[ticker]["name"] = ""
            
    return loaded_data


